var sg = require('../index.js')(process.env.SENDGRID_APIKEY);

var email = new sg.Email();

email.to = 'eddiezane@sendgrid.com';
email.subject = 'Testing addBcc';
email.from = 'taco@cat.limo';
email.html = '<div>Our old logo:<img src="cid:logo-cid"></div><div>Our new logo:<img src="cid:new-logo-cid"></div>';

email.addFile({
  cid: 'logo-cid',
  url: 'https://keen.io/docs/integrations/sendgrid/sendgrid_logo.png',
  filename: 'old-logo.png'
});
  // path: './logo.png',

email.addFile({
  cid: 'new-logo-cid',
  path: './new-sendgrid-logo.png',
  filename: 'new-logo.png'
});

sg.send(email, function(err, json) {
  if (err) console.error(err);
  console.log(json);
});
